mapper.delete:

DELETE FROM SP_WORK_LINE
 WHERE W_ID = #{wid}
   AND P_ID = #{pid}
   AND U_NODE_ID = #{unodeId}
   AND D_NODE_ID = #{dnodeId}

mapper.select:

SELECT ID,
       W_ID,
       P_ID,
       U_NODE_ID,
       D_NODE_ID,
       EXPRESSION,
       LINE_DESC,
       IS_MUST_PASS
  FROM SP_WORK_LINE
 WHERE 1 = 1


 AND ID = #{id} 


 AND W_ID = #{wid} 


 AND P_ID = #{pid} 


 AND U_NODE_ID = #{unodeId} 


 AND D_NODE_ID = #{dnodeId} 


 AND IS_MUST_PASS = #{isMustPass}
