mapper.select:
SELECT B.B_ID,
       B.B_NAME,
       B.B_TYPE,
       B.B_STATUS,
       B.B_CODE,
       B.BELONG_TO_SYS,
       B.P_ID,
       B.REL_DETAIL,
       C.gnmkmc as BELONG_TO_SYS_NAME
  FROM SP_BUSINESS B
  LEFT JOIN niutal_XTGL_JSGNMKDMB C 
    ON B.BELONG_TO_SYS = C.gnmkdm


		 B.B_ID = #{bid} 


		 AND B.B_NAME = #{bname} 


		 AND B.B_TYPE = #{btype} 


		 AND B.B_STATUS = #{bstatus} 


		 AND B.BELONG_TO_SYS = #{belongToSys} 


		 AND B.B_TYPE = #{btype} 


		 AND B.B_CODE = #{bcode}
