mapper.update:

UPDATE SP_LINE
   SET U_NODE_ID   = #{unodeId},
       D_NODE_ID   = #{dnodeId}
 WHERE LINE_ID = #{lineId}


UPDATE SP_LINE
   SET U_NODE_ID   = #{unodeId},
       D_NODE_ID   = #{dnodeId},
       LINE_DESC   = #{lineDesc,jdbcType=VARCHAR},
       IS_MUST_PASS= #{isMustPass,jdbcType=VARCHAR}
 WHERE LINE_ID = #{lineId}


UPDATE SP_LINE
   SET EXPRESSION  = #{expression}
 WHERE LINE_ID = #{lineId}

mapper.select:

SELECT COUNT(LINE_ID)
  FROM SP_LINE
 WHERE U_NODE_ID = #{unodeId}
   AND D_NODE_ID = #{dnodeId}


SELECT LINE_ID, P_ID, U_NODE_ID, D_NODE_ID, EXPRESSION, LINE_DESC, IS_MUST_PASS
  FROM SP_LINE
 WHERE P_ID = #{pid}


SELECT LINE_ID, P_ID, U_NODE_ID, D_NODE_ID, EXPRESSION, LINE_DESC, IS_MUST_PASS
  FROM SP_LINE
 WHERE LINE_ID = #{0}

SELECT LINE_ID, P_ID, U_NODE_ID, D_NODE_ID, EXPRESSION, LINE_DESC, IS_MUST_PASS
  FROM SP_LINE
 WHERE 1=1

	 AND LINE_ID = #{lineId} 


	 AND P_ID = #{pid} 


	 AND U_NODE_ID = #{unodeId} 


	 AND D_NODE_ID = #{dnodeId} 


	 AND IS_MUST_PASS = #{isMustPass}
